INSERT INTO `yorumlar` (`ID`, `uyeID`, `urunID`, `puan`, `metin`, `durum`, `tarih`) VALUES
(1, 1, 6, 5, 'ANİTA DELUXE oturma grubu gerçekten çok kaliteli! Kumaş kalitesi ve işçilik mükemmel. Salonumuza çok yakıştı, misafirlerimiz çok beğeniyor. Satın aldığıma çok memnunum, kesinlikle tavsiye ederim.', 1, '2024-12-15'),
(2, 1, 7, 4, 'LUNA köşe takımı güzel ama rengi beklediğimden biraz farklıydı. Konfor açısından çok iyi, oturmak keyifli. Kargo biraz geç geldi ama genel olarak memnunum.', 1, '2024-12-14'),
(3, 1, 8, 5, 'Zümrüt masa sandalye takımı harika! Açılır masa çok pratik, misafir geldiğinde çok işe yarıyor. Sandalyeler rahat ve sağlam. Kurulum da kolaydı. Fiyatına göre çok kaliteli.', 1, '2024-12-13'),
(4, 1, 9, 4, 'Lal banklı masa takımı güzel tasarımlı. Altın detaylar çok şık duruyor. Bank çok kullanışlı, çocuklar çok seviyor. Sadece montaj biraz zor oldu ama sonuç mükemmel.', 1, '2024-12-12'),
(5, 1, 10, 5, 'Mada yatak odası takımı tam aradığımız gibiydi! Modern tasarım ve kaliteli malzeme. Dolap içi düzenlemesi çok iyi düşünülmüş. Eşim ve ben çok memnun kaldık.', 1, '2024-12-11'),
(6, 1, 21, 5, 'ASUS laptop performansı gerçekten iyi! Oyun oynayabiliyorum, iş için de mükemmel. SSD sayesinde çok hızlı açılıyor. Ekran kalitesi de güzel. Bu fiyata harika bir bilgisayar.', 1, '2024-12-10'),
(7, 1, 22, 4, 'LG televizyon görüntü kalitesi çok iyi, 4K netliği harika. Smart özellikler kullanışlı. Sadece uzaktan kumanda biraz karışık ama alışınca sorun yok. Genel olarak memnunum.', 1, '2024-12-09'),
(8, 1, 6, 3, 'Oturma grubu güzel ama renk solmaya başladı. 6 ay oldu aldığımızdan beri. Konfor iyi ama kalite beklediğim gibi değil. Fiyatı da pahalıydı açıkçası.', 0, '2024-12-08'),
(9, 1, 7, 5, 'LUNA köşe takımı salonumuzun tam ihtiyacıydı! Köşe kullanımı çok pratik, alan tasarrufu sağlıyor. Kumaş kalitesi de iyi, leke tutmuyor. Çocuklarla bile rahatça kullanabiliyoruz.', 1, '2024-12-07'),
(10, 1, 8, 4, 'Masa sandalye takımı güzel ama sandalyelerden biri biraz sallanıyor. Müşteri hizmetlerini aradım, çözüm bulacaklarını söylediler. Genel görünüm güzel, misafirlerin beğenisini topluyor.', 1, '2024-12-06'),
(11, 1, 21, 5, 'Laptop mükemmel! Üniversite için aldım, hem ders hem eğlence için harika. Pil ömrü uzun, taşıması kolay. GeForce ekran kartı sayesinde grafik işleri de yapabiliyorum. Çok memnunum.', 1, '2024-12-05'),
(12, 1, 22, 5, 'Televizyonun görüntü kalitesi inanılmaz! Netflix izlerken sinema keyfi yaşıyoruz. Ses kalitesi de çok iyi. 70 inç tam salona uygun boyut. Alışverişin en iyisiydi kesinlikle.', 1, '2024-12-04'),
(13, 1, 9, 2, 'Masa takımı güzel ama bank çok sert. Uzun süre oturmak zor. Ayrıca montaj talimatları çok karışıktı, profesyonel yardım almak zorunda kaldık. Fiyatına göre beklentimi karşılamadı.', 0, '2024-12-03'),
(14, 1, 10, 4, 'Yatak odası takımı modern ve şık. Dolap çok geniş, eşyalarımız rahatça sığıyor. Sadece yatak başlığı biraz sert ama genel olarak kaliteli. Montaj ekibi çok profesyoneldi.', 1, '2024-12-02'),
(15, 1, 6, 5, 'ANİTA DELUXE gerçekten deluxe! Kumaş çok yumuşak ve kaliteli. Berjerler de çok rahat. Misafirlerimiz sürekli takım hakkında soru soruyor. Fiyatı değer kesinlikle.', 1, '2024-12-01'),
(16, 1, 21, 3, 'Laptop genel olarak iyi ama fan sesi biraz yüksek. Oyun oynarken rahatsız edici oluyor. Performans yeterli ama bu fiyata daha sessiz olabilirdi. Servisi de yavaş.', 0, '2024-11-30'),
(17, 1, 22, 5, 'LG TV harika! Ailemizle birlikte film gecelerinin vazgeçilmezi oldu. Görüntü netliği ve renk kalitesi mükemmel. Smart özellikler çok kullanışlı, YouTube ve Netflix rahat izliyoruz.', 1, '2024-11-29'),
(18, 1, 7, 4, 'Köşe takımı oturma konforunu artırdı. L şeklinde oturma imkanı çok praktik. Kumaş kalitesi iyi, temizlemesi kolay. Sadece montaj biraz zaman aldı ama sonuç güzel oldu.', 1, '2024-11-28');

